import styled from 'styled-components';

export const TopSec = styled.div`
  color: #fff;
  background: #101522;
  Min-height: 400px;
  text-align: center;
  padding-top: 2%;
  outline: 2px solid #4b59f7;
`;

export const InfoRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  flex-direction: ${({ imgStart }) => (imgStart ? 'row-reverse' : 'row')};

`;

export const Column2 = styled.div`
  margin-bottom: 15px;
  padding-right: 3%;
  padding-left: 3%;
  flex: 1;
  max-width: 30%;
  flex-basis: 50%;

  @media screen and (max-width: 768px) {
    max-width: 100%;
    flex-basis: 100%;
    display: flex;
    justify-content: center;
  }
`;

export const Column1 = styled.div`
  margin-bottom: 15px;
  padding-right: 15px;
  padding-left: 15px;
  flex: 1;
  max-width: 70%;
  flex-basis: 50%;

  @media screen and (max-width: 768px) {
    max-width: 100%;
    flex-basis: 100%;
    display: flex;
    justify-content: center;
  }
`;
